pragma solidity >=0.8.0 <0.9.0;

import "./Structures.sol";

contract DoctorManagement is Structures{

    // Doctors set their profile
    function setDoctorProfile(string memory _name, string memory _major) public payable {

        require(!isDoctor[msg.sender], "Doctor profile already exists!");
        require(!isPatient[msg.sender], "You are already a patient!");

        for (uint i = 0; i < requestedDoctorList.length; i++) {
          if (msg.sender == requestedDoctorList[i]) {
            revert("You already requested to be a doctor");
          }
        }

        Doctor storage doctor = doctors[msg.sender];
        doctor.name = _name;
        doctor.major = _major;

        requestedDoctorList.push(msg.sender);
    }

    // Request from doctors
    function requestAccess(address _patient) public payable {

      require(isDoctor[msg.sender], "Only doctors can request access");
      require(isPatient[_patient], "The address should be a patient");

      Patient storage patient = patients[_patient];

      for (uint i = 0; i < patient.requestedDoctors.length; i++) {
        if (msg.sender == patient.requestedDoctors[i]) {
            revert("You have already required access");
        }
      }

      for (uint i = 0; i < patient.doctorsList.length; i++) {
        if (msg.sender == patient.doctorsList[i]) {
            revert("You are already in the doctor list");
        }
      }

      patients[_patient].requestedDoctors.push(msg.sender);
    }

    // Doctor retrieves list of patients
    function getPatientsList(address _doctor) public view returns(address[] memory) {

        return doctors[_doctor].patientsList;
    }

    // Authorized doctors add record to patient's profile
    function addRecord(address _patient, string memory _description, string memory _diagnosis, string memory _treatment, string memory _imageUrl) public onlyAuthorizedDoctors(_patient) {

        require(isDoctor[msg.sender], "Only doctors can create new record");
        require(isPatient[_patient], "That address is not a patient");

        Patient storage patient = patients[_patient];

        patient.records.push(Record({
            id: patient.recordCount,
            description: _description,
            diagnosis: _diagnosis,
            treatment: _treatment,
            imageUrl: _imageUrl,
            createdTimestamp: block.timestamp
        }));

        patient.recordCount++;

        emit RecordAdded(_patient, msg.sender, block.timestamp);
    }

    // Retrieve requested appointments
    function getRequestedAppointments(address _doctor) public view returns(Appointment[] memory) {

        require (isDoctor[_doctor], "Only doctors can access requested appointments");

        return doctors[_doctor].requestedAppointments;
    }

    // Accept appointment
    function acceptAppointment(address _patient) public {

        require(isDoctor[msg.sender], "Only doctors can accept appointments");

        Appointment[] storage requested = doctors[msg.sender].requestedAppointments;

        for (uint i = 0; i < requested.length; i++) {
            if (requested[i].patientaddr == _patient) {

                sortAppointments(doctors[msg.sender].appointments, requested[i]);
                sortAppointments(patients[_patient].appointments, requested[i]);

                requested[i] = requested[requested.length - 1];
                requested.pop();

                break;
            }
        }
    }

    // Sort appointments according to time
    function sortAppointments(Appointment[] storage appointments, Appointment memory requested) private {

        uint point = 0;
        bool shift = false;

        for (uint i = 0; i < appointments.length; i++) {
            if (requested.date < appointments[i].date || (requested.date == appointments[i].date && requested.time <= appointments[i].time)) {
                point = i;
                shift = true;
                break;
            }
        }

        appointments.push(requested);

        if (shift == true) {

            for (uint i = appointments.length - 1; i > point; i--) {
                appointments[i] = appointments[i - 1];
            }

            appointments[point] = requested;
        }
    }
}","keccak256":"0xa2045ed7aab5b5349e42ef8bca09e5c41cb3cf2b71be1a2d3cdb17d7a791a544","license":"MIT"},"contracts/HealthcareSystem.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Structures.sol";
import "./DoctorManagement.sol";
import "./PatientManagement.sol";
import "./AdminManagement.sol";

contract HealthcareSystem is Structures, DoctorManagement, PatientManagement, AdminManagement {

    constructor () {
        isAdmin[0x49745EF0F5E06f3e0d3F6CcB590F100726202011] = true;
    }

// Functions for both

    // Get appointments
    function getAppointments(address _addr) public view returns(Appointment[] memory) {
        if (isPatient[_addr]) {
            return patients[_addr].appointments;
        } else if (isDoctor[_addr]) {
            return doctors[_addr].appointments;
        }

        Appointment[] memory emptyAppointments = new Appointment[](0);

        return emptyAppointments;
    }

    // Get Patient's info
    function getPatientInfo(address _doctor, address _patient) public view
        returns(
            string memory name,
            string memory phone,
            string memory dob,
            string memory gender,
            string memory bloodType,
            string memory height,
            string memory weight,
            string memory houseAddr,
            string memory allergies,
            Record[] memory records,
            uint recordCount
        ) 
    {

        if (isDoctor[_doctor]) {
            require(patients[_patient].authorizedDoctors[_doctor], "You need to be authorized to view this patient's record");
        } else {
            require(_doctor == _patient);   // Both address is from the patient, who want to view their own record
        }

        Patient storage patient = patients[_patient];

        return (
            patient.name,
            patient.phone,
            patient.dob,
            patient.gender,
            patient.bloodType,
            patient.height,
            patient.weight,
            patient.houseAddr,
            patient.allergies,
            patient.records,
            patient.recordCount
        );
    }

    // Get Doctor's info
    function getDoctorInfo(address _patient, address _doctor) public view 
        returns(
            string memory name,
            string memory major
        ) 
    {
        if (isDoctor[_doctor]  && isPatient[_patient]) {
            require(patients[_patient].authorizedDoctors[_doctor], "This doctor is not authorized to work with you");
        } else {
            require(_doctor == _patient);   // Both address is from the doctor, who want to view their own profle or the admin
        }


        Doctor storage doctor = doctors[_doctor];

        return (
            doctor.name,
            doctor.major
        );
    }
}","keccak256":"0x459f4d168af6f4b618f2d305183a37e3fb9bf071e685bcfcfc1f3d4617c4f548","license":"MIT"},"contracts/PatientManagement.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Structures.sol";

contract PatientManagement is Structures {

    // Patients set their profile
    function setPatientProfile(
        string memory _name,
        string memory _phone,
        string memory _dob,
        string memory _gender,
        string memory _bloodType,
        string memory _height,
        string memory _weight,
        string memory _houseAddr,
        string memory _allergies
    ) public payable {
        require(!isPatient[msg.sender], "Patient profile already exists");
        require(!isDoctor[msg.sender], "You are already a doctor!");

        Patient storage patient = patients[msg.sender];

        patient.name = _name;
        patient.phone = _phone;
        patient.dob = _dob;
        patient.gender = _gender;
        patient.bloodType = _bloodType;
        patient.height = _height;
        patient.weight = _weight;
        patient.houseAddr = _houseAddr;
        patient.allergies = _allergies;
        patient.recordCount = 0;

        isPatient[msg.sender] = true;

        patientList.push(msg.sender);
    }

    // Patient retrieves requested doctors
    function getRequestedDoctors(address _patient) public view returns(address[] memory) {

        //require(isPatient[_patient], "This should be a patient");

        Patient storage patient = patients[_patient];

        return patient.requestedDoctors;
    }

    // Patients grant access to doctors
    function grantAccess(address _doctor) public {

        require(isPatient[msg.sender], "Only patients can grant access");

        address[] storage requested = patients[msg.sender].requestedDoctors;

        bool check = false;

        for (uint i = 0; i < requested.length; i++) {
          if (requested[i] == _doctor) {
            requested[i] = requested[requested.length - 1];
            requested.pop();
            check = true;
            break;
          }
        }

        if (check == true) {
            patients[msg.sender].authorizedDoctors[_doctor] = true;       // Authorize this doctor

            doctors[_doctor].patientsList.push(msg.sender);               // Add this patient to the list of the doctor

            patients[msg.sender].doctorsList.push(_doctor);              // Add this doctor to the list of the patient

        } else {
            emit GrantAccessFailed(msg.sender, _doctor);
        }
    }

    // Patients revoke access to doctors
    function revokeAccess(address _doctors) public {

        require(isPatient[msg.sender], "Only patients can revoke access");

        patients[msg.sender].authorizedDoctors[_doctors] = false;
    }

    // Patient retrieves list of doctors
    function getDoctorsList(address _patient) public view returns(address[] memory) {

        Patient storage patient = patients[_patient];

        return patient.doctorsList;
    }

    // Request Appointments
    function requestAppointment(address _doctor, uint _date, uint _time, string memory _description) public {

        require(patients[msg.sender].authorizedDoctors[_doctor], "This doctor is not authorized to work with you");

        Doctor storage doctor = doctors[_doctor];

        for (uint i = 0; i < doctor.requestedAppointments.length; i++) {
            if (msg.sender == doctor.requestedAppointments[i].patientaddr) {
                revert ("You already requested this doctor");
            }
        }

        for (uint i = 0; i < doctor.appointments.length; i++) {
            if (msg.sender == doctor.appointments[i].patientaddr) {
                revert ("You already have an appointment with this doctor");
            }
        }

        doctor.requestedAppointments.push(Appointment ({
            patientaddr: msg.sender,
            doctoraddr: _doctor,
            date: _date,
            time: _time,
            description: _description
        }));
    }
}","keccak256":"0xe92324a99751bf2b47c8c86b1c4b8e283f9380e17f880097d39129280098d9c5","license":"MIT"},"contracts/Structures.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract Structures {
    struct Patient {
        string name;
        string phone;
        string dob;
        string gender;
        string bloodType;
        string height;
        string weight;
        string houseAddr;
        string allergies;
        Record[] records;
        uint recordCount;
        mapping(address => bool) authorizedDoctors;
        address[] requestedDoctors;
        address[] doctorsList;
        Appointment[] appointments;
    }

    struct Doctor {
        string name;
        string major;
        address[] patientsList;
        Appointment[] requestedAppointments;
        Appointment[] appointments;
    }

    struct Admin {
        string name;
    }

    struct Record {
        uint id;
        string description;
        string diagnosis;
        string treatment;
        string imageUrl;
        uint createdTimestamp;
    }

    struct Appointment {
        address patientaddr;
        address doctoraddr;
        uint date;
        uint time;
        string description;
    }

    address[] public patientList;
    address[] public doctorList;
    address[] public requestedDoctorList;

    mapping(address => Patient) public patients;
    mapping(address => Doctor) public doctors;

    mapping(address => bool) public isDoctor;
    mapping(address => bool) public isPatient;
    mapping(address => bool) public isAdmin;

    event RecordAdded(address patient, address doctor, uint timestamp);
    event RecordUpdated(address patient, address doctor, uint timestamp);
    event GrantAccessFailed(address patient, address doctor);
    event AppointmentRequested(address patient, address doctor, string date, string time);

    // Check if the healthcare doctors are authorized
    modifier onlyAuthorizedDoctors(address _patient) {
        require(
            patients[_patient].authorizedDoctors[msg.sender],
            "You are not authorized to view this record"
        );
        _;
    }

    // Only the admin is authorized
    modifier onlyAdmin(address _admin) {
      require(_admin == 0x49745EF0F5E06f3e0d3F6CcB590F100726202011, "Not authorized: Only the admin can perform this action");
      _;
    }
}","keccak256":"0x9d6c7fc5ab6fad26e79d3445430af6f16015493f84a3b641e056e325a66393a3","license":"MIT"}},"version":1}
